/**
 * Base64 encoded notification sound for the timer
 * This is a short "ding" sound in MP3 format
 */
export const NOTIFICATION_SOUND_BASE64 = "";

// Convert base64 string to blob URL
export function createNotificationSound(): string {
  // Convert base64 to blob
  const byteCharacters = atob(NOTIFICATION_SOUND_BASE64);
  const byteArrays = [];
  
  for (let offset = 0; offset < byteCharacters.length; offset += 512) {
    const slice = byteCharacters.slice(offset, offset + 512);
    
    const byteNumbers = new Array(slice.length);
    for (let i = 0; i < slice.length; i++) {
      byteNumbers[i] = slice.charCodeAt(i);
    }
    
    const byteArray = new Uint8Array(byteNumbers);
    byteArrays.push(byteArray);
  }
  
  const blob = new Blob(byteArrays, { type: 'audio/mp3' });
  return URL.createObjectURL(blob);
}